import { SnapshotJson } from './__json__/snapshot-json';

/**
 * Snapshot for a VM or vApp.
 */
/* istanbul ignore next: autogenerated */
export class Snapshot {

  constructor(private _json: SnapshotJson) {
  }

  /**
   * Get creation date.
   * @returns {number}
   */
  get creationDate(): Date {
    return new Date(this._json.creation_date);
  }

  /**
   * Get size.
   * @returns {number}
   */
  get size(): number {
    return this._json.size;
  }

  /**
   * Get powered on.
   * @returns {boolean}
   */
  get poweredOn(): boolean {
    return this._json.powered_on;
  }

  /**
   * Get the json representation of this class.
   * @returns {SnapshotJson}
   */
  get json(): SnapshotJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
