import { SearchResultJson } from './__json__/search-result-json';

/**
 * Company search result class.
 */
/* istanbul ignore next: autogenerated */
export class SearchResult {

  constructor(private _json: SearchResultJson) {}

  /**
   * Get query.
   * @returns {string}
   */
  get query(): string {
    return this._json.query;
  }

  /**
   * Get offset.
   * @returns {number}
   */
  get offset(): number {
    return this._json.offset;
  }

  /**
   * Get limit.
   * @returns {number}
   */
  get limit(): number {
    return this._json.limit;
  }

  /**
   * Get returned hits.
   * @returns {number}
   */
  get returnedHits(): number {
    return this._json.returned_hits;
  }

  /**
   * Get total hits.
   * @returns {number}
   */
  get totalHits(): number {
    return this._json.total_hits;
  }

  /**
   * Get results.
   * @returns {Array<{ [key: string]: string }>}
   */
  get results(): Array<{ [key: string]: string }> {
    return this._json.results;
  }

  /**
   * Get the json representation of this class.
   * @returns {SearchResultJson}
   */
  get json(): SearchResultJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
