import { UserCreationJson } from './__json__/user-creation-json';

/* istanbul ignore next: autogenerated */
export class UserCreation {

  constructor(private _json: UserCreationJson) {
  }

  /**
   * Get username.
   * @returns {string}
   */
  get username(): string {
    return this._json.username;
  }

  /**
   * Get created.
   * @returns {boolean}
   */
  get created(): boolean {
    return this._json.created;
  }

  /**
   * Get error.
   * @returns {string}
   */
  get error(): string {
    return this._json.error;
  }

  /**
   * Get the json representation of this class.
   * @returns {UserCreationJson}
   */
  get json(): UserCreationJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
