import { CRLJson } from './__json__/crl-json';
import { X509CRL } from './x509-crl';

/* istanbul ignore next: autogenerated */
export class CRL {

  constructor(private _json: CRLJson) {
  }

  /**
   * Get object id.
   * @returns {string}
   */
  get objectId(): string {
    return this._json.object_id;
  }

  /**
   * Get description.
   * @returns {string}
   */
  get description(): string {
    return this._json.description;
  }

  /**
   * Get scope.
   * @returns {string}
   */
  get scope(): string {
    return this._json.scope;
  }

  /**
   * Get is temporal.
   * @returns {boolean}
   */
  get isTemporal(): boolean {
    return this._json.is_temporal;
  }

  /**
   * Get issuer cn.
   * @returns {string}
   */
  get issuerCn(): string {
    return this._json.issuer_cn;
  }

  /**
   * Get pem encoding.
   * @returns {string}
   */
  get pemEncoding(): string {
    return this._json.pem_encoding;
  }

  /**
   * Get x509 crl.
   * @returns {X509CRL}
   */
  get x509CRL(): X509CRL {
    return new X509CRL(this._json.x509_crl);
  }

  /**
   * Get the json representation of this class.
   * @returns {CRLJson}
   */
  get json(): CRLJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
