import { SelfSignCertificateRequestJson } from './__json__/self-sign-certificate-request-json';

/* istanbul ignore next: autogenerated */
export class SelfSignCertificateRequest {

  private readonly _json: SelfSignCertificateRequestJson;

  constructor(selfSignCertificateRequest: SelfSignCertificateRequest);
  constructor(selfSignCertificateRequestJson: SelfSignCertificateRequestJson);
  constructor(noOfDays: number);
  constructor(firstParam: number | SelfSignCertificateRequest | SelfSignCertificateRequestJson) {
    if (typeof firstParam === 'number') {
      // Parameters constructor
      this._json = {
        no_of_days: firstParam
      } as SelfSignCertificateRequestJson;
    } else if (firstParam instanceof SelfSignCertificateRequest) {
      // Copy constructor
      this._json = (firstParam as SelfSignCertificateRequest).json;
    } else {
      // Json or empty constructor
      this._json = (firstParam || {}) as SelfSignCertificateRequestJson;
    }
  }

  /**
   * Get no of days.
   * @returns {number}
   */
  get noOfDays(): number {
    return this._json.no_of_days;
  }

  /**
   * Get the json representation of this class.
   * @returns {SelfSignCertificateRequestJson}
   */
  get json(): SelfSignCertificateRequestJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
