import { VnicSubInterfaceJson } from './__json__/vnic-sub-interface-json';
import { AddressGroup } from './vnic-address-group';

/* istanbul ignore next: autogenerated */
export class VnicSubInterface {

  constructor(private _json: VnicSubInterfaceJson) {
  }

  /**
   * Get is connected.
   * @returns {boolean}
   */
  get isConnected(): boolean {
    return this._json.is_connected;
  }

  /**
   * Get label.
   * @returns {string}
   */
  get label(): string {
    return this._json.label;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get index.
   * @returns {number}
   */
  get index(): number {
    return this._json.index;
  }

  /**
   * Get tunnel id.
   * @returns {number}
   */
  get tunnelId(): number {
    return this._json.tunnel_id;
  }

  /**
   * Get vlan id.
   * @returns {number}
   */
  get vlanId(): number {
    return this._json.vlan_id;
  }

  /**
   * Get enable send redirects.
   * @returns {boolean}
   */
  get enableSendRedirects(): boolean {
    return this._json.enable_send_redirects;
  }

  /**
   * Get mtu.
   * @returns {number}
   */
  get mtu(): number {
    return this._json.mtu;
  }

  /**
   * Get address groups.
   * @returns {Array<AddressGroup>}
   */
  get addressGroups(): Array<AddressGroup> {
    return this._json.address_groups.map(it => new AddressGroup(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {VnicSubInterfaceJson}
   */
  get json(): VnicSubInterfaceJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
