import { BillField, BillingSampleJson } from './__json__/billing-sample-json';

/* istanbul ignore next: autogenerated */
export class BillingSample {

  constructor(private _json: BillingSampleJson) {
  }

  /**
   * Get time.
   * @returns {number}
   */
  get time(): number {
    return this._json.time;
  }

  /**
   * Get value.
   * @returns {number}
   */
  get value(): number {
    return this._json.value;
  }

  /**
   * Get additional fields.
   * @returns {{ [key: BillField]: number }}
   */
  get additionalFields(): { [key in BillField]: number } {
    return this._json.additional_fields;
  }

  /**
   * Get the json representation of this class.
   * @returns {BillingSampleJson}
   */
  get json(): BillingSampleJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
