import { SsoLoginErrorJson } from './__json__/sso-login-error-json';

/**
 * SSO Login Error Class
 */
/* istanbul ignore next: autogenerated */
export class SsoLoginError {

  constructor(private _json: SsoLoginErrorJson) {
  }

  /**
   * The invalid username that was passed from the identity provider when
   * attempting to log in via SSO.
   * @returns {string}
   */
  get invalidUsername(): string {
    return this._json.invalid_username;
  }

  /**
   * The time the error occurred.
   * @returns {Date}
   */
  get date(): Date {
    return this._json.date;
  }

  /**
   * Get the json representation of this class.
   * @returns {SsoLoginErrorJson}
   */
  get json(): SsoLoginErrorJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
